@use 'mixins/mixins' as *;
@use 'mixins/var' as *;
@use 'common/var' as *;

@include b(popper) {
  @include set-component-css-var('popper', $popper);
}

@include b(popper) {
  position: absolute;
  border-radius: getCssVar('popper', 'border-radius');
  padding: 5px 11px;
  z-index: 2000;
  font-size: 12px;
  line-height: 20px;
  min-width: 10px;
  overflow-wrap: break-word;
  visibility: visible;

  $arrow-selector: #{& + '__arrow'};

  @include when(dark) {
    color: getCssVar('bg-color');
    background: getCssVar('text-color', 'primary');
    border: 1px solid getCssVar('text-color', 'primary');

    #{$arrow-selector}::before {
      border: 1px solid getCssVar('text-color', 'primary');
      background: getCssVar('text-color', 'primary');
      right: 0;
    }
  }

  @include when(light) {
    background: getCssVar('bg-color', 'overlay');
    border: 1px solid getCssVar('border-color', 'light');

    #{$arrow-selector}::before {
      border: 1px solid getCssVar('border-color', 'light');
      background: getCssVar('bg-color', 'overlay');
      right: 0;
    }
  }

  @include when(pure) {
    padding: 0;
  }

  @include e(arrow) {
    position: absolute;
    width: 10px;
    height: 10px;
    z-index: -1;

    &::before {
      position: absolute;
      width: 10px;
      height: 10px;
      z-index: -1;
      content: ' ';
      transform: rotate(45deg);
      background: getCssVar('text-color', 'primary');
      box-sizing: border-box;
    }
  }

  $placements: (
    'top': 'bottom',
    'bottom': 'top',
    'left': 'right',
    'right': 'left',
  );

  @each $placement, $opposite in $placements {
    &[data-popper-placement^='#{$placement}'] > #{$arrow-selector} {
      #{$opposite}: -5px;

      &::before {
        @if $placement == top {
          border-bottom-right-radius: 2px;
        }
        @if $placement == bottom {
          border-top-left-radius: 2px;
        }
        @if $placement == left {
          border-top-right-radius: 2px;
        }
        @if $placement == right {
          border-bottom-left-radius: 2px;
        }
      }
    }
  }

  @each $placement,
    $adjacency
      in ('top': 'left', 'bottom': 'right', 'left': 'bottom', 'right': 'top')
  {
    &[data-popper-placement^='#{$placement}'] {
      #{$arrow-selector}::before {
        border-#{$placement}-color: transparent !important;
        border-#{$adjacency}-color: transparent !important;
      }
    }
  }
}

:root {
  --el-color-primary: #fff;
  --el-bg-color-overlay:transparent;
}

/* Element Chalk Variables */
.el-popper {
  --el-popper-border-radius: var(--el-popover-border-radius, 4px);
}

.el-popper {
  border-radius: var(--el-popper-border-radius);
  font-size: 12px;
  line-height: 20px;
  min-width: 10px;
  overflow-wrap: break-word;
  padding: 5px 11px;
  position: absolute;
  visibility: visible;
  z-index: 2000;
}
.el-popper.is-dark {
  background: var(--el-text-color-primary);
  border: 1px solid var(--el-text-color-primary);
  color: var(--el-bg-color);
}
.el-popper.is-dark .el-popper__arrow:before {
  background: var(--el-text-color-primary);
  border: 1px solid var(--el-text-color-primary);
  right: 0;
}

.el-popper.is-light {
  background: var(--el-bg-color-overlay);
  border: 1px solid var(--el-border-color-light);
}
.el-popper.is-light .el-popper__arrow:before {
  background: var(--el-bg-color-overlay);
  border: 1px solid var(--el-border-color-light);
  right: 0;
}

.el-popper.is-pure {
  padding: 0;
}

.el-popper__arrow {
  height: 10px;
  position: absolute;
  width: 10px;
  z-index: -1;
}
.el-popper__arrow:before {
  background: var(--el-text-color-primary);
  box-sizing: border-box;
  content: " ";
  height: 10px;
  position: absolute;
  transform: rotate(45deg);
  width: 10px;
  z-index: -1;
}

.el-popper[data-popper-placement^=top]>.el-popper__arrow {
  bottom: -5px;
}
.el-popper[data-popper-placement^=top]>.el-popper__arrow:before {
  border-bottom-right-radius: 2px;
}
.el-popper[data-popper-placement^=bottom]>.el-popper__arrow {
  top: -5px;
}
.el-popper[data-popper-placement^=bottom]>.el-popper__arrow:before {
  border-top-left-radius: 2px;
}
.el-popper[data-popper-placement^=left]>.el-popper__arrow {
  right: -5px;
}
.el-popper[data-popper-placement^=left]>.el-popper__arrow:before {
  border-top-right-radius: 2px;
}
.el-popper[data-popper-placement^=right]>.el-popper__arrow {
  left: -5px;
}
.el-popper[data-popper-placement^=right]>.el-popper__arrow:before {
  border-bottom-left-radius: 2px;
}
.el-popper[data-popper-placement^=top] .el-popper__arrow:before {
  border-left-color: transparent !important;
  border-top-color: transparent !important;
}
.el-popper[data-popper-placement^=bottom] .el-popper__arrow:before {
  border-bottom-color: transparent !important;
  border-right-color: transparent !important;
}
.el-popper[data-popper-placement^=left] .el-popper__arrow:before {
  border-bottom-color: transparent !important;
  border-left-color: transparent !important;
}
.el-popper[data-popper-placement^=right] .el-popper__arrow:before {
  border-right-color: transparent !important;
  border-top-color: transparent !important;
}

