transforms.result:
@use 'sass:map';

@use 'mixins/mixins' as *;
@use 'mixins/var' as *;
@use 'common/var' as *;

@include b(scrollbar) {
  @include set-component-css-var('scrollbar', $scrollbar);
}

@include b(scrollbar) {
  overflow: hidden;
  position: relative;
  height: 100%;

  @include e(wrap) {
    overflow: auto;
    height: 100%;

    @include m(hidden-default) {
      scrollbar-width: none;
      &::-webkit-scrollbar {
        display: none;
      }
    }
  }

  @include e(thumb) {
    position: relative;
    display: block;
    width: 0;
    height: 0;
    cursor: pointer;
    border-radius: inherit;
    background-color: var(
      #{getCssVarName('scrollbar-bg-color')},
      map.get($scrollbar, 'bg-color')
    );
    transition: getCssVar('transition-duration') background-color;
    opacity: var(
      #{getCssVarName('scrollbar-opacity')},
      map.get($scrollbar, 'opacity')
    );

    &:hover {
      background-color: var(
        #{getCssVarName('scrollbar-hover-bg-color')},
        map.get($scrollbar, 'hover-bg-color')
      );
      opacity: var(
        #{getCssVarName('scrollbar-hover-opacity')},
        map.get($scrollbar, 'hover-opacity')
      );
    }
  }

  @include e(bar) {
    position: absolute;
    right: 2px;
    bottom: 2px;
    z-index: 1;
    border-radius: 4px;

    @include when(vertical) {
      width: 6px;
      top: 2px;

      > div {
        width: 100%;
      }
    }

    @include when(horizontal) {
      height: 6px;
      left: 2px;

      > div {
        height: 100%;
      }
    }
  }
}

.#{$namespace}-scrollbar-fade {
  &-enter-active {
    transition: opacity 340ms ease-out;
  }

  &-leave-active {
    transition: opacity 120ms ease-out;
  }

  &-enter-from,
  &-leave-active {
    opacity: 0;
  }
}

:root {
  --el-color-primary: #fff;
  --el-bg-color-overlay:transparent;
}

/* Element Chalk Variables */
.el-scrollbar {
  --el-scrollbar-opacity: 0.3;
  --el-scrollbar-bg-color: var(--el-text-color-secondary);
  --el-scrollbar-hover-opacity: 0.5;
  --el-scrollbar-hover-bg-color: var(--el-text-color-secondary);
}

.el-scrollbar {
  height: 100%;
  overflow: hidden;
  position: relative;
}
.el-scrollbar__wrap {
  height: 100%;
  overflow: auto;
}
.el-scrollbar__wrap--hidden-default {
  scrollbar-width: none;
}
.el-scrollbar__wrap--hidden-default::-webkit-scrollbar {
  display: none;
}

.el-scrollbar__thumb {
  background-color: var(--el-scrollbar-bg-color, var(--el-text-color-secondary));
  border-radius: inherit;
  cursor: pointer;
  display: block;
  height: 0;
  opacity: var(--el-scrollbar-opacity, .3);
  position: relative;
  transition: var(--el-transition-duration) background-color;
  width: 0;
}
.el-scrollbar__thumb:hover {
  background-color: var(--el-scrollbar-hover-bg-color, var(--el-text-color-secondary));
  opacity: var(--el-scrollbar-hover-opacity, .5);
}

.el-scrollbar__bar {
  border-radius: 4px;
  bottom: 2px;
  position: absolute;
  right: 2px;
  z-index: 1;
}
.el-scrollbar__bar.is-vertical {
  top: 2px;
  width: 6px;
}
.el-scrollbar__bar.is-vertical>div {
  width: 100%;
}

.el-scrollbar__bar.is-horizontal {
  height: 6px;
  left: 2px;
}
.el-scrollbar__bar.is-horizontal>div {
  height: 100%;
}

.el-scrollbar-fade-enter-active {
  transition: opacity .34s ease-out;
}
.el-scrollbar-fade-leave-active {
  transition: opacity .12s ease-out;
}
.el-scrollbar-fade-enter-from,.el-scrollbar-fade-leave-active {
  opacity: 0;
}
